import React from 'react';
import axios from 'axios';

class ChatApp extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      messages: [],
      messageInput: ''
    };
  }

  handleMessageSubmit = async (e) => {
    e.preventDefault();
    const message = this.state.messageInput.trim();
    if (message) {
      const messages = [...this.state.messages, message];
      this.setState({ messages, messageInput: '' });

      try {
        const response = await axios.post('https://api.openai.com/v1/engines/davinci-codex/completions', {
          prompt: message,
          max_tokens: 100,
          n: 1,
          stop: ['\n']
        }, {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer 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`
          }
        });

        const aiMessage = response.data.choices[0].text.trim();
        if (aiMessage) {
          const messages = [...this.state.messages, aiMessage];
          this.setState({ messages });
        }
      } catch (error) {
        console.error(error);
      }
    }
  };

  handleMessageChange = (e) => {
    this.setState({ messageInput: e.target.value });
  };

  render() {
    return (
      <div style={{ 
        display: 'flex', 
        flexDirection: 'column', 
        alignItems: 'center', 
        justifyContent: 'center', 
        height: '100vh', 
        backgroundColor: 'black', 
        color: 'green' 
      }}>
        <h1 style={{ 
          marginTop: '1em', 
          fontFamily: 'Retro Font1', 
          fontSize: '3em', 
          textShadow: '2px 2px 4px #000000' 
        }}>Chat App</h1>
        <div style={{ 
          flex: 1, 
          display: 'flex', 
          flexDirection: 'column', 
          justifyContent: 'flex-end', 
          alignItems: 'flex-start', 
          paddingBottom: '1em', 
          textAlign: 'left', 
          width: '100%', 
          paddingLeft: '1em' 
        }}>
          {this.state.messages.map((message, index) => (
            <div key={index} style={{ 
              marginBottom: '0.5em', 
              textAlign: 'left', 
              fontFamily: 'Retro Font2', 
              fontSize: '1.5em', 
              textShadow: '1px 1px 2px #000000' 
            }}>{message}</div>
          ))}
        </div>
        <form onSubmit={this.handleMessageSubmit} style={{ 
          marginBottom: '1em', 
          width: '100%', 
          display: 'flex', 
          justifyContent: 'flex-start' 
        }}>
          <input
            type="text"
            placeholder="Type a message..."
            value={this.state.messageInput}
            onChange={this.handleMessageChange}
            style={{ 
              flex: 1, 
              padding: '0.5em', 
              textAlign: 'left', 
              fontFamily: 'Retro Font2', 
              fontSize: '1.5em', 
              backgroundColor: 'black', 
              color: 'green', 
              border: 'none', 
              outline: 'none' 
            }}
          />
          <button type="submit" style={{ display: 'none' }}>Send</button>
        </form>
      </div>
    );
  }
}

export default ChatApp;
