import React from 'react';
import axios from 'axios';

class ChatApp extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      messages: [],
      messageInput: ''
    };
  }

  handleMessageSubmit = async (e) => {
    e.preventDefault();
    const message = this.state.messageInput.trim();

    //Set messages state

    if (message) {
      const messages = [...this.state.messages, message];
      this.setState({ messages, messageInput: '' });

    }
    
    this.sendMessage(userMessage)
  };

  handleMessageChange = (e) => {
    this.setState({ messageInput: e.target.value });
  };

  sendMessage = async (userMessage) => {
    const openaiEndpoint = 'https://api.openai.com/v1/chat/completions';
    const openaiApiKey = "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";
    const model = 'gpt-3.5-turbo';
    
    const headers = {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${openaiApiKey}`
    };
    
    const data = {
      'model': model,
      'messages': [{"role": "user", "content": userMessage}]
    };
    
    axios.post(openaiEndpoint, data, { headers })
    .then(response => {
      const chatResponse = response.data.choices[0].message.content;
      console.log(chatResponse);
      // Do something with the chat response
    })
    .catch(error => {
      console.error(error);
      // Handle the error
    });
  };
  
  

  render() {
    return (
      <div style={{ 
        display: 'flex', 
        flexDirection: 'column', 
        alignItems: 'center', 
        justifyContent: 'center', 
        height: '100vh', 
        backgroundColor: 'black', 
        color: 'green' 
      }}>
        <h1 style={{ 
          marginTop: '1em', 
          fontFamily: 'Retro Font1', 
          fontSize: '3em', 
          textShadow: '2px 2px 4px #000000' 
        }}>Chat App</h1>
        <div style={{ 
          flex: 1, 
          display: 'flex', 
          flexDirection: 'column', 
          justifyContent: 'flex-end', 
          alignItems: 'flex-start', 
          paddingBottom: '1em', 
          textAlign: 'left', 
          width: '100%', 
          paddingLeft: '1em' 
        }}>
          {this.state.messages.map((message, index) => (
            <div key={index} style={{ 
              marginBottom: '0.5em', 
              textAlign: 'left', 
              fontFamily: 'Retro Font2', 
              fontSize: '1.5em', 
              textShadow: '1px 1px 2px #000000' 
            }}>{message}</div>
          ))}
        </div>
        <form onSubmit={this.handleMessageSubmit} style={{ 
          marginBottom: '1em', 
          width: '100%', 
          display: 'flex', 
          justifyContent: 'flex-start' 
        }}>
          <input
            type="text"
            placeholder="Type a message..."
            value={this.state.messageInput}
            onChange={this.handleMessageChange}
            style={{ 
              flex: 1, 
              padding: '0.5em', 
              textAlign: 'left', 
              fontFamily: 'Retro Font2', 
              fontSize: '1.5em', 
              backgroundColor: 'black', 
              color: 'green', 
              border: 'none', 
              outline: 'none' 
            }}
          />
          <button type="submit" style={{ display: 'none' }}>Send</button>
        </form>
      </div>
    );
  }
}

export default ChatApp;
